using Microsoft.AspNetCore.Http;
using Microsoft.AspNetCore.Mvc;
using System.Collections.Generic;
using System.Linq;

using WebApplication21.DataDB;
using WebApplication21.Models;

namespace WebApplication21.Controllers
{
    [Route("api/[controller]")]
    [ApiController]
    public class ValuesController : ControllerBase
    {
        DataContextHouses house_DB;
        public ValuesController(DataContextHouses _dataContext)
        {
            house_DB = _dataContext;   
            if(!house_DB.Houses.Any())
            {
                house_DB.Houses.Add(new House { Id=1, Desc = "Elit House 1", Img= "data:image/jpeg;base64," });
            }
        }
        // GET api/values
        [HttpGet]
        public IEnumerable<IHouse> Get()
        {
            return house_DB.Houses;
        }

        public IHouse GetHouseById(int id)
        {
            House house = house_DB.Houses.FirstOrDefault(x => x.Id == id);
            if(house == null)
                return (IHouse)NotFound();
            return house;
        }

